SELECT

FROM d_redpackets_distribute



        AND id = #{id}


        AND redpackets_id = #{redpacketsId}


        AND member_id = #{memberId}


        AND name = #{name}


        AND money = #{money}


        AND valid_time = #{validTime}


        AND status = #{status}


        AND create_time >= #{createTimeBegin}


        AND create_time <= #{createTimeEnd}


        AND update_time >= #{updateTimeBegin}


        AND update_time <= #{updateTimeEnd}

SELECT money, (select member_name from d_member where member_id = a.member_id) member_name, create_time, status, update_time, (select order_id from d_order where rcb_id = a.redpackets_id) order_id FROM d_redpackets_distribute a
status != 'unreceive' and redpackets_id = #{redpacketsId} AND member_id = (select member_id from d_member where member_name = #{memberName}) AND status = #{status}
limit #{pageInfo.begin}, #{pageInfo.end}
